import 'package:flutter/material.dart';
import 'screens/movie.dart'; // Assure-toi d'importer le fichier de ta page
import 'models/movie.dart';

void main() {
  runApp(MyApp());
}

class MyApp extends StatelessWidget {
  Film monFilm = Film(
    title: 'Film de test',
    description: 'Un film épique de fantasy réalisé par Peter Jackson.',
    posterUrl:
        'data:image/jpeg;base64,',
    actors: ['Elijah Wood', 'Ian McKellen', 'Viggo Mortensen'],
  );

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Mon Application',
      routes: {
        '/': (context) => MaPagePrincipale(),
        '/film_detail': (context) => MovieDetailScreen(film: monFilm),
      },
    );
  }
}

class MaPagePrincipale extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Ma Page Principale'),
      ),
      body: Center(
        child: ElevatedButton(
          onPressed: () {
            Navigator.pushNamed(context, '/film_detail');
          },
          child: Text('Afficher les détails du film'),
        ),
      ),
    );
  }
}
